INSERT INTO `aldryn_bootstrap3_bootstrap3carouselslideplugin` VALUES (235, '', '', NULL, NULL, '', '', '<h1>Dativa is now Deductive</h1>\n\n<p>Deductive is a Data Services company focusing on applications of granular TV data. We help data providers get their data to market. We help customers make effective use of data. And we offer productized data services for Attribution and Measurement.</p>\n\n<p><a class=\"btn btn-info btn-lg\" href=\"/about-us/\">Learn more</a></p>', '', 306, NULL, NULL, '{\"alt\": \"Data Engineering\"}');
INSERT INTO `aldryn_bootstrap3_bootstrap3carouselslideplugin` VALUES (236, '', '', NULL, NULL, '', '', '<h1>Attribution</h1>\n\n<p>Deductive Attribution evaluates an ad campaign’s success in reaching target audiences with specific creative efforts to drive measurable results. Our attribution solution uses proprietary combinatorial analysis to quantify which ad investments contribute most to campaign results.</p>\n\n<p><a class=\"btn btn-info btn-lg\" href=\"/our-services/advanced-advertising/\">Learn more</a></p>', '', 346, NULL, NULL, '{\"alt\": \"Data Science\"}');
INSERT INTO `aldryn_bootstrap3_bootstrap3carouselslideplugin` VALUES (237, '', '', NULL, NULL, '', '', '<h1>Data Services</h1>\n\n<p>Deductive Data Services include panel construction and management, generation and management of weights to optimize panel representativeness, demographic append services, data matching, data generation, data anonymization and data publishing.</p>\n\n<p><a class=\"btn btn-info btn-lg\" href=\"/data-science/\">Learn more</a></p>', '', 345, NULL, NULL, '{\"alt\": \"Data Operations\"}');
INSERT INTO `aldryn_bootstrap3_bootstrap3carouselslideplugin` VALUES (8292, '', '', NULL, NULL, '', '', '<h1>Professional Services</h1>\n\n<p>Deductive provides data centric professional services to clients ranging from data generators, including ACR and STB companies, to data platform providers to end users of data such as networks, agencies and advertisers. In all cases, we help clients put data to work.</p>\n\n<p><a class=\"btn btn-info btn-lg\" href=\"/data-engineering/\">Learn more</a></p>', '', 305, NULL, NULL, '{\"alt\": \"Data Operations\"}');
