INSERT INTO `djangocms_text_ckeditor_text` VALUES (9465, '<h1>Data Publishing Services</h1>');
INSERT INTO `djangocms_text_ckeditor_text` VALUES (9467, '<p data-renderer-start-pos=\"191\">Deductive builds and manages custom data publishing solutions for data providers. We have built real time data publishing platforms for Fortune 500 companies. Data publishing platform construction typically involves some or all of the following processes:</p>\n\n<p data-renderer-start-pos=\"445\">· Data Ingest from multiple, often heterogeneous sources</p>\n\n<p data-renderer-start-pos=\"503\">· Data Source Service Level Agreement instrumentation and reporting</p>\n\n<p data-renderer-start-pos=\"572\">· Data Cleaning and Normalization</p>\n\n<p data-renderer-start-pos=\"607\">· Data Matching and Enrichment</p>\n\n<p data-renderer-start-pos=\"639\">· Rules-based Data Management for regulatory and contractual compliance</p>\n\n<p data-renderer-start-pos=\"712\">· Data Anonymization for Privacy Compliance</p>\n\n<p data-renderer-start-pos=\"757\">· Data Security</p>\n\n<p data-renderer-start-pos=\"774\">· Data Access Controls, including user, use case and field level controls</p>');
