INSERT INTO `djangocms_text_ckeditor_text` VALUES (10761, '<p>Client problem/need: Customer had licensed both STB and smart TV datasets and wanted to combine the two together to have the best possible model.</p>');
INSERT INTO `djangocms_text_ckeditor_text` VALUES (10766, '<p>Deductive solution: We did a detailed comparison of the two datasets and built a model to create a single panel of national viewership based upon a combination of the two datasets. We tested and compared the results against industry benchmarks and implemented a daily production workflow.</p>');
INSERT INTO `djangocms_text_ckeditor_text` VALUES (10771, '<p>Outcome: Customer has switched measurement and reporting to the new panel and significantly increased revenues</p>');
