INSERT INTO `djangocms_text_ckeditor_text` VALUES (10947, '<p>Client problem/need: Customer had invested in TV viewing data and wanted to try to replace external attribution systems with an in-house platform.</p>');
INSERT INTO `djangocms_text_ckeditor_text` VALUES (10952, '<p>Deductive solution: We devised an approach and tested it against their existing results. Using our data science expertise and their in-house datasets we were able to create a more compelling and much more cost-effective solution.</p>');
INSERT INTO `djangocms_text_ckeditor_text` VALUES (10957, '<p>Outcome: Customer now runs all of one-class of attribution in-house and is looking to extend out to other classes over time.</p>');
